import React, { useState } from 'react';
import ListGroup from 'react-bootstrap/ListGroup'
import {useSelector, useDispatch } from 'react-redux';
import { socketRef } from '../socket/socket';
import { actions } from '../redux/actions/actions';


function Members() {
    const dispatch=useDispatch();

    const setMembersToStore = (member) => {
        dispatch(actions.setDisableMember(member.name))
        dispatch(actions.setLinesDisable(member.name))
      }
    
        
      socketRef.on("newMember",(arrNames)=>{
        
        dispatch(actions.setMembers(arrNames))
    // dispatch(actions.setMembers({name:name,isDisable:false}));

        // setListMembers([...listMembers,{name}])
        // dispatch(actions.setMeers(name))
        })

//   const [listMembers, setListMembers] = useState([]);
  const members=useSelector((state)=>state.member.members)
  const name=useSelector((state)=>state.member.name)
  
  return ( <ListGroup as="ul">
  {   members.map((member)=>{
      if(member.name!=name){   
      return(  
         <div> {member.isDisable
        ? <ListGroup.Item as="li" 
        style={{backgroundColor:'gray', cursor: 'pointer'}}  onClick={(e)=>{setMembersToStore(member)}} >
        {member.name}
       </ListGroup.Item>
       :<ListGroup.Item as="li" 
       style={{backgroundColor:'white', cursor: 'pointer'}}onClick={(e)=>{setMembersToStore(member)}} >
       {member.name}
      </ListGroup.Item>
    }</div>
        
    ) } })}
</ListGroup>
     
 
    );
  
}

export default Members;
