import React, { useState } from 'react';
import ListGroup from 'react-bootstrap/ListGroup'
import {useSelector, useDispatch } from 'react-redux';
import { socketRef } from '../socket/socket';
import { actions } from '../redux/actions/actions';


function Members() {
    let isDisable=false;
    const dispatch=useDispatch();

    const setMembersToStore = (member) => {
        console.log("arrive",member);
        dispatch(actions.setDisableMember(member))
      }
    
    
  const [listMembers, setListMembers] = useState([]);
  const members=useSelector((state)=>state.member.members)
  console.log("mmnmn",members);
  //const isUserRegister = useSelector(state => state.registerReducer.isUserRegister);
  //  const counter = useSelector((state) => state.counter)

  
  return ( 
  <>
  <div> style={{width:"300px"}}
  <ListGroup as="ul">
  {   members.map((member)=>{
   
      return(
          
         <div> {member.isDisable
        ? <ListGroup.Item as="li" style={{color:'red'}} active onclick={(e)=>{setMembersToStore(e.target.value)}} >
        {member.name}
       </ListGroup.Item>

       :<ListGroup.Item as="li" style={{color:'red'}} disabled onclick={(e)=>{e.target.style.backgroundColor = "blue"}} >
       {member.name}
      </ListGroup.Item>
    }</div>
        
    )  })}
</ListGroup>
   </div>  
 </>
    );
    socketRef.on("newMember",(name)=>{
        setListMembers([...listMembers,{name}])
        })
        
}

export default Members;
